import React, { useState, useEffect, useCallback } from 'react';
import './App.css';

const App = () => {
  const [gridSize, setGridSize] = useState({ rows: 15, cols: 20 });
  const [wavePosition, setWavePosition] = useState(0);
  const [waveDirection, setWaveDirection] = useState(1);
  const [colorPhase, setColorPhase] = useState(0);
  const [isPlaying, setIsPlaying] = useState(true);
  const [speed, setSpeed] = useState(150);

  // Generate 4-grid color pattern with smooth wave transitions
  const generateWavePattern = useCallback((row, time) => {
    const waveWidth = 6; // Wave width for each grid section
    const centerPosition = wavePosition;

    return Array.from({ length: gridSize.cols }, (_, col) => {
      // Calculate distance from wave center
      const distanceFromCenter = Math.abs(col - centerPosition);

      // Dynamic wave width with smoother variations
      const dynamicWaveWidth = waveWidth + Math.sin(time * 0.005) * 2 + Math.cos(time * 0.008) * 1.5;
      const waveIntensity = Math.max(0, 1 - Math.pow(distanceFromCenter / (dynamicWaveWidth / 2), 1.2));

      if (waveIntensity > 0) {
        // Create gradient within the wave
        const gradientPosition = (col - (centerPosition - dynamicWaveWidth/2)) / dynamicWaveWidth;
        const clampedGradient = Math.max(0, Math.min(1, gradientPosition));

        // Determine which of the 4 grid sections this cell belongs to
        const gridSection = Math.floor((col / gridSize.cols) * 4);
        const sectionOffset = (gridSection * 90) % 360; // 90 degrees per section

        // Enhanced dynamic effects with section-specific variations
        const rowEffect = Math.sin((row * 0.3) + (time * 0.01)) * 0.4 + 0.6;
        const timeEffect = Math.sin((time * 0.02) + (col * 0.08)) * 0.3 + 0.7;
        const waveEffect = Math.sin((distanceFromCenter * 0.3) + (time * 0.012)) * 0.2 + 0.8;

        // Section-specific color phase influence
        const sectionPhase = (time * 0.015) + (gridSection * Math.PI / 2);
        const colorPhaseEffect = Math.sin(sectionPhase + (row * 0.2) + (col * 0.1)) * 0.15 + 0.85;

        // Combine all effects for smooth intensity
        const dynamicIntensity = waveIntensity * rowEffect * timeEffect * waveEffect * colorPhaseEffect;

        // Enhanced gradient with section-specific variations
        const gradientVariation = Math.sin(time * 0.02 + row * 0.25 + (gridSection * 0.5)) * 0.3 + 0.7;
        const dynamicGradient = clampedGradient * gradientVariation;

        return {
          intensity: Math.max(0, Math.min(1, dynamicIntensity * (0.4 + 0.6 * clampedGradient))),
          gradientPos: Math.max(0, Math.min(1, dynamicGradient)),
          gridSection: gridSection // Add section info for color generation
        };
      }
      return { intensity: 0, gradientPos: 0, gridSection: 0 };
    });
  }, [wavePosition, gridSize.cols, colorPhase]);

  // Generate colors for 4 distinct grid sections
  const getColor = (intensity, gradientPos, phase, gridSection = 0) => {
    if (intensity === 0) return '#1a1a1a'; // Dark background for empty cells

    // Smoother color transitions - more cycles for gradual changes
    const totalCycles = 480; // 8 seconds at 100ms intervals for smoother transitions
    const normalizedPhase = phase / totalCycles;

    // Define base colors for each of the 4 grid sections
    const sectionColors = [
      { baseHue: 0, name: 'Red' },      // Section 0: Red spectrum
      { baseHue: 90, name: 'Green' },   // Section 1: Green spectrum  
      { baseHue: 180, name: 'Blue' },   // Section 2: Blue spectrum
      { baseHue: 270, name: 'Purple' }  // Section 3: Purple spectrum
    ];

    const currentSection = sectionColors[gridSection % 4];

    // Create smooth hue rotation within each section's color range
    const sectionHueRange = 60; // 60 degrees range per section
    const baseHue = currentSection.baseHue + (normalizedPhase * sectionHueRange * 2) % sectionHueRange;
    const hueVariation = Math.sin(normalizedPhase * Math.PI * 4 + gridSection * Math.PI / 2) * 20;
    const gradientHue = gradientPos * 30; // Reduced gradient spread for section clarity

    // Smooth hue calculation with section-specific variations
    const hue = (baseHue + hueVariation + gradientHue) % 360;

    // Section-specific saturation and lightness
    const saturationBase = 75 + gridSection * 5; // Slightly different saturation per section
    const saturationVariation = Math.sin(normalizedPhase * Math.PI * 3 + gridSection * Math.PI / 3) * 15;
    const saturation = Math.max(70, Math.min(100, saturationBase + saturationVariation + intensity * 20));

    const lightnessBase = 40 + gridSection * 8; // Different lightness per section
    const lightnessVariation = Math.sin(normalizedPhase * Math.PI * 2 + gridSection * Math.PI / 4) * 15;
    const lightness = Math.max(35, Math.min(80, lightnessBase + lightnessVariation + intensity * 25));

    // Add subtle alpha for depth
    const alpha = 0.9 + intensity * 0.1;

    return `hsla(${hue}, ${saturation}%, ${lightness}%, ${alpha})`;
  };

  // Animation loop
  useEffect(() => {
    if (!isPlaying) return;

    const interval = setInterval(() => {
      setWavePosition(prev => {
        const newPos = prev + waveDirection;
        // Reverse direction when reaching edges: start to last, then last to start
        if (newPos >= gridSize.cols - 3) {
          setWaveDirection(-1); // Reverse to move left
          return gridSize.cols - 3;
        } else if (newPos <= 3) {
          setWaveDirection(1); // Reverse to move right
          return 3;
        }
        return newPos;
      });

      setColorPhase(prev => (prev + 1) % 480); // 480 cycles = 8 seconds at 100ms intervals for smoother transitions
    }, speed);

    return () => clearInterval(interval);
  }, [isPlaying, waveDirection, gridSize.cols, speed]);

  const handleGridSizeChange = (type, value) => {
    const newValue = Math.max(5, Math.min(30, parseInt(value) || 5));
    setGridSize(prev => ({
      ...prev,
      [type]: newValue
    }));
  };

  const togglePlayPause = () => {
    setIsPlaying(prev => !prev);
  };

  const resetWave = () => {
    setWavePosition(3); // Start from near the beginning
    setWaveDirection(1); // Move right initially
    setColorPhase(0);
  };

  return (
    <div className="app">
      <div className="header">
        <h1 className="title">✨ Bouncing Wave Grid</h1>
        <div className="controls">
          <div className="control-group">
            <label>📏 Rows: {gridSize.rows}</label>
            <input
              type="range"
              min="5"
              max="30"
              value={gridSize.rows}
              onChange={(e) => handleGridSizeChange('rows', e.target.value)}
            />
          </div>
          <div className="control-group">
            <label>📐 Cols: {gridSize.cols}</label>
            <input
              type="range"
              min="5"
              max="30"
              value={gridSize.cols}
              onChange={(e) => handleGridSizeChange('cols', e.target.value)}
            />
          </div>
          <div className="control-group">
            <label>⚡ Speed: {speed}ms</label>
            <input
              type="range"
              min="50"
              max="500"
              value={speed}
              onChange={(e) => setSpeed(parseInt(e.target.value))}
            />
          </div>
          <button onClick={togglePlayPause} className="play-button">
            {isPlaying ? '⏸️ Pause' : '▶️ Play'}
          </button>
          <button onClick={resetWave} className="reset-button">
            🔄 Reset
          </button>
        </div>
      </div>

      <div className="grid-container">
        <div 
          className="grid"
          style={{
            gridTemplateColumns: `repeat(${gridSize.cols}, 1fr)`,
            gridTemplateRows: `repeat(${gridSize.rows}, 1fr)`
          }}
        >
          {Array.from({ length: gridSize.rows }, (_, row) =>
            generateWavePattern(row, colorPhase).map((cell, col) => (
              <div
                key={`${row}-${col}`}
                className="grid-cell"
                style={{
                  backgroundColor: getColor(cell.intensity, cell.gradientPos, colorPhase, cell.gridSection),
                  boxShadow: cell.intensity > 0 
                    ? `0 0 ${cell.intensity * 10}px ${getColor(cell.intensity, cell.gradientPos, colorPhase, cell.gridSection)}`
                    : 'none'
                }}
              />
            ))
          )}
        </div>
      </div>

      <div className="info-panel">
        <div className="stats">
          <div className="stat">
            <span className="stat-label">📍 Wave Position</span>
            <span className="stat-value">{wavePosition.toFixed(1)}</span>
          </div>
          <div className="stat">
            <span className="stat-label">🎨 Color Phase</span>
            <span className="stat-value">{Math.floor(colorPhase / 80) + 1}/6</span>
          </div>
          <div className="stat">
            <span className="stat-label">⏱️ Next Color</span>
            <span className="stat-value">{Math.ceil((480 - colorPhase) / 80)}s</span>
          </div>
          <div className="stat">
            <span className="stat-label">🧭 Direction</span>
            <span className="stat-value">{waveDirection > 0 ? '→' : '←'}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
