import React, { useState, useEffect, useCallback } from 'react';
import './App.css';

const App = () => {
  const [gridSize, setGridSize] = useState({ rows: 15, cols: 20 });
  const [wavePosition, setWavePosition] = useState(0);
  const [waveDirection, setWaveDirection] = useState(1);
  const [colorPhase, setColorPhase] = useState(0);
  const [isPlaying, setIsPlaying] = useState(true);
  const [speed, setSpeed] = useState(100);
  const [colorChangeTimer, setColorChangeTimer] = useState(0);

  // Generate wave pattern for each row - now uses all grids
  const generateWavePattern = useCallback((row, time) => {
    const waveHeight = gridSize.cols; // Use all columns for the wave
    const centerPosition = wavePosition;
    const waveSpread = Math.sin((row * 0.3) + (time * 0.01)) * 2;

    return Array.from({ length: gridSize.cols }, (_, col) => {
      // Create a wave that spans the entire width with varying intensity
      const waveIntensity = Math.sin((col * 0.5) + (time * 0.02) + (row * 0.1)) * 0.5 + 0.5;
      const distanceFromCenter = Math.abs(col - centerPosition);
      const centerIntensity = Math.max(0, 1 - (distanceFromCenter / (gridSize.cols * 0.3)));

      const finalIntensity = waveIntensity * (0.3 + 0.7 * centerIntensity);
      const gradientPosition = col / gridSize.cols;

      return {
        intensity: finalIntensity,
        gradientPos: gradientPosition
      };
    });
  }, [wavePosition, gridSize.cols]);

  // Generate color based on phase and position
  const getColor = (intensity, gradientPos, phase) => {
    if (intensity === 0) return '#000000';

    const hue = (phase * 60 + gradientPos * 120) % 360;
    const saturation = 80 + intensity * 20;
    const lightness = 20 + intensity * 40;

    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
  };

  // Animation loop
  useEffect(() => {
    if (!isPlaying) return;

    const interval = setInterval(() => {
      setWavePosition(prev => {
        const newPos = prev + waveDirection;
        if (newPos >= gridSize.cols - 3 || newPos <= 3) {
          setWaveDirection(prevDir => -prevDir);
        }
        return Math.max(3, Math.min(gridSize.cols - 3, newPos));
      });

      setColorPhase(prev => (prev + 1) % 360);
    }, speed);

    return () => clearInterval(interval);
  }, [isPlaying, waveDirection, gridSize.cols, speed]);

  const handleGridSizeChange = (type, value) => {
    const newValue = Math.max(5, Math.min(30, parseInt(value) || 5));
    setGridSize(prev => ({
      ...prev,
      [type]: newValue
    }));
  };

  const togglePlayPause = () => {
    setIsPlaying(prev => !prev);
  };

  const resetWave = () => {
    setWavePosition(Math.floor(gridSize.cols / 2));
    setWaveDirection(1);
    setColorPhase(0);
  };

  return (
    <div className="app">
      <div className="header">
        <h1 className="title">🌊 Bouncing Wave Grid</h1>
        <div className="controls">
          <div className="control-group">
            <label>Rows: {gridSize.rows}</label>
            <input
              type="range"
              min="5"
              max="30"
              value={gridSize.rows}
              onChange={(e) => handleGridSizeChange('rows', e.target.value)}
            />
          </div>
          <div className="control-group">
            <label>Cols: {gridSize.cols}</label>
            <input
              type="range"
              min="5"
              max="30"
              value={gridSize.cols}
              onChange={(e) => handleGridSizeChange('cols', e.target.value)}
            />
          </div>
          <div className="control-group">
            <label>Speed: {speed}ms</label>
            <input
              type="range"
              min="50"
              max="500"
              value={speed}
              onChange={(e) => setSpeed(parseInt(e.target.value))}
            />
          </div>
          <button onClick={togglePlayPause} className="play-button">
            {isPlaying ? '⏸️ Pause' : '▶️ Play'}
          </button>
          <button onClick={resetWave} className="reset-button">
            🔄 Reset
          </button>
        </div>
      </div>

      <div className="grid-container">
        <div 
          className="grid"
          style={{
            gridTemplateColumns: `repeat(${gridSize.cols}, 1fr)`,
            gridTemplateRows: `repeat(${gridSize.rows}, 1fr)`
          }}
        >
          {Array.from({ length: gridSize.rows }, (_, row) =>
            generateWavePattern(row, colorPhase).map((cell, col) => (
              <div
                key={`${row}-${col}`}
                className="grid-cell"
                style={{
                  backgroundColor: getColor(cell.intensity, cell.gradientPos, colorPhase),
                  boxShadow: cell.intensity > 0 
                    ? `0 0 ${cell.intensity * 10}px ${getColor(cell.intensity, cell.gradientPos, colorPhase)}`
                    : 'none'
                }}
              />
            ))
          )}
        </div>
      </div>

      <div className="info-panel">
        <div className="stats">
          <div className="stat">
            <span className="stat-label">Wave Position:</span>
            <span className="stat-value">{wavePosition.toFixed(1)}</span>
          </div>
          <div className="stat">
            <span className="stat-label">Color Phase:</span>
            <span className="stat-value">{colorPhase}°</span>
          </div>
          <div className="stat">
            <span className="stat-label">Direction:</span>
            <span className="stat-value">{waveDirection > 0 ? '→' : '←'}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
